import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { useCookies } from "react-cookie"; // Import useCookies
import api from "../utils/api"; // Custom Axios instance
import BarChart from "../components/BarChart";
import LineChart from "../components/LineChart";
import Logout from "../components/Logout";
import FilterBar from "../components/FilterBar";

const Dashboard = () => {
  const navigate = useNavigate();
  const [cookies, setCookie, removeCookie] = useCookies(["age", "gender", "start_date", "end_date"]); // Use react-cookie

  // Initialize filters from cookies or default values
  const [filters, setFilters] = useState({
    age: cookies.age || "",
    gender: cookies.gender || "",
    start_date: cookies.start_date || "",
    end_date: cookies.end_date || "",
  });

  const [data, setData] = useState([]);

  // Update cookies whenever filters change
  useEffect(() => {
    setCookie("age", filters.age, { path: "/", maxAge: 604800 }); // 7 days
    setCookie("gender", filters.gender, { path: "/", maxAge: 604800 });
    setCookie("start_date", filters.start_date, { path: "/", maxAge: 604800 });
    setCookie("end_date", filters.end_date, { path: "/", maxAge: 604800 });
  }, [filters, setCookie]);

  // Fetch data based on filters
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await api.get("/api/data", { params: filters });
        setData(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
        alert("Failed to fetch data!");
      }
    };

    fetchData();
  }, [filters]);

  // Apply filters and update URL (optional)
  const applyFilters = () => {
    navigate(`/api/data?${new URLSearchParams(filters).toString()}`, { replace: true });
  };

  // Clear preferences (filters and cookies)
  const clearPreferences = () => {
    removeCookie("age", { path: "/" });
    removeCookie("gender", { path: "/" });
    removeCookie("start_date", { path: "/" });
    removeCookie("end_date", { path: "/" });
    setFilters({
      age: "",
      gender: "",
      start_date: "",
      end_date: "",
    });
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="p-6 bg-blue-500 text-white flex justify-between items-center">
        <h1 className="text-2xl font-semibold">
          Good afternoon, {localStorage.getItem("user_name") || "User"}
        </h1>
        <Logout />
      </div>

      <div className="p-8 bg-white shadow-md">
        <h1 className="text-2xl font-semibold mb-4">Dashboard</h1>

        {/* Filters */}
        <FilterBar filters={filters} setFilters={setFilters} />

        <div className="flex justify-between mt-4">
          <button
            onClick={applyFilters}
            className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
          >
            Apply
          </button>
          <button
            onClick={clearPreferences}
            className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
          >
            Reset Preferences
          </button>
        </div>
      </div>

      <div className="p-4 grid grid-cols-1 sm:grid-cols-2 gap-4">
        {data.length > 0 ? (
          <>
            <div className="bg-white p-4 rounded shadow-md">
              <h2 className="text-lg font-semibold mb-2 text-center">Bar Chart</h2>
              <BarChart data={data} />
            </div>
            <div className="bg-white p-4 rounded shadow-md">
              <h2 className="text-lg font-semibold mb-2 text-center">Line Chart</h2>
              <LineChart data={data} />
            </div>
          </>
        ) : (
          <p>No data available.</p>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
