import React, { useEffect, useState } from "react";
import { useSearchParams, useNavigate } from "react-router-dom";
import api from "../utils/api"; // Custom Axios instance
import BarChart from "../components/BarChart";
import LineChart from "../components/LineChart";

const Dashboard = () => {
  const [data, setData] = useState([]);
  const [searchParams, setSearchParams] = useSearchParams();
  const navigate = useNavigate();

  // Extract query parameters from the URL
  const ageParam = searchParams.get("age") || "";
  const genderParam = searchParams.get("gender") || "";
  const startDateParam = searchParams.get("start_date") || "";
  const endDateParam = searchParams.get("end_date") || "";

  const [filters, setFilters] = useState({
    age: ageParam,
    gender: genderParam,
    start_date: startDateParam,
    end_date: endDateParam,
  });

  // Fetch data based on query parameters
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await api.get("/api/data", { params: filters });
        setData(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
        alert("Failed to fetch data!");
      }
    };

    fetchData();
  }, [filters]);

  // Update filters and URL when the "Apply" button is clicked
  const applyFilters = () => {
    setSearchParams(filters); // Update URL with current filters
    navigate(`/api/data?${new URLSearchParams(filters).toString()}`); // Redirect to sharable URL
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="p-4 bg-white shadow-md">
        <h1 className="text-2xl font-semibold mb-4">Dashboard</h1>
        {/* Filter Form */}
        <div className="grid grid-cols-2 gap-4 sm:grid-cols-4 mb-4">
          <select
            value={filters.age}
            onChange={(e) => setFilters({ ...filters, age: e.target.value })}
            className="border border-gray-300 rounded p-2"
          >
            <option value="">Select Age</option>
            <option value="15-25">15-25</option>
            <option value=">25">">2</option>
          </select>
          <select
            value={filters.gender}
            onChange={(e) => setFilters({ ...filters, gender: e.target.value })}
            className="border border-gray-300 rounded p-2"
          >
            <option value="">Select Gender</option>
            <option value="Male">Male</option>
            <option value="Female">Female</option>
          </select>
          <input
            type="date"
            value={filters.start_date}
            onChange={(e) => setFilters({ ...filters, start_date: e.target.value })}
            className="border border-gray-300 rounded p-2"
          />
          <input
            type="date"
            value={filters.end_date}
            onChange={(e) => setFilters({ ...filters, end_date: e.target.value })}
            className="border border-gray-300 rounded p-2"
          />
        </div>
        <button
          onClick={applyFilters}
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          Apply
        </button>
      </div>

      {/* Charts */}
      <div className="p-4">
        <BarChart data={data} />
        <LineChart data={data} />
      </div>
    </div>
  );
};

export default Dashboard;
