import React, { useEffect, useState } from "react";
import { useSearchParams, useNavigate } from "react-router-dom";
import api from "../utils/api"; // Custom Axios instance
import BarChart from "../components/BarChart";
import LineChart from "../components/LineChart";

const Dashboard = () => {
  const [data, setData] = useState([]);
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();

  const [filters, setFilters] = useState({
    age: searchParams.get("age") || "",
    gender: searchParams.get("gender") || "",
    start_date: searchParams.get("start_date") || "",
    end_date: searchParams.get("end_date") || "",
  });

  // Fetch user_name from localStorage
  const userName = localStorage.getItem("user_name") || "User";

  // Redirect to login if token is not present
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login", { replace: true });
    }
  }, [navigate]);

  // Fetch data based on filters
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await api.get("/api/data", { params: filters });
        setData(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
        alert("Failed to fetch data!");
      }
    };

    fetchData();
  }, [filters]);

  // Update filters and URL when the "Apply" button is clicked
  const applyFilters = () => {
    navigate(`/api/data?${new URLSearchParams(filters).toString()}`, { replace: true });
  };

  // Logout handler
  const handleLogout = async () => {
    try {
      // Optionally, call your logout API endpoint
      await api.post("/auth/logout");

      // Clear localStorage
      localStorage.clear();

      // Clear all cookies
      document.cookie.split(";").forEach((cookie) => {
        const name = cookie.split("=")[0].trim();
        document.cookie = `${name}=;expires=Thu, 01 Jan 1970 00:00:00 UTC;path=/;`;
      });

      // Redirect to login page
      navigate("/login", { replace: true });
    } catch (error) {
      console.error("Error during logout:", error);
      alert("Logout failed!");
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="p-6 bg-blue-500 text-white flex justify-between items-center">
        <h1 className="text-2xl font-semibold">Good afternoon, {userName}</h1>
        <button
          onClick={handleLogout}
          className="bg-red-500 px-4 py-2 rounded hover:bg-red-600"
        >
          Logout
        </button>
      </div>

      <div className="p-8 bg-white shadow-md">
        <h1 className="text-2xl font-semibold mb-4">Dashboard</h1>
        <div className="grid grid-cols-2 gap-4 sm:grid-cols-4 mb-4">
          <select
            value={filters.age}
            onChange={(e) => setFilters({ ...filters, age: e.target.value })}
            className="border border-gray-300 rounded p-2"
          >
            <option value="">Select Age</option>
            <option value="15-25">15-25</option>
            <option value=">25">&gt;25</option>
          </select>
          <select
            value={filters.gender}
            onChange={(e) => setFilters({ ...filters, gender: e.target.value })}
            className="border border-gray-300 rounded p-2"
          >
            <option value="">Select Gender</option>
            <option value="Male">Male</option>
            <option value="Female">Female</option>
          </select>
          <input
            type="date"
            value={filters.start_date}
            onChange={(e) => setFilters({ ...filters, start_date: e.target.value })}
            className="border border-gray-300 rounded p-2"
          />
          <input
            type="date"
            value={filters.end_date}
            onChange={(e) => setFilters({ ...filters, end_date: e.target.value })}
            className="border border-gray-300 rounded p-2"
          />
        </div>
        <button
          onClick={applyFilters}
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          Apply
        </button>
      </div>

      <div className="p-4 grid grid-cols-1 sm:grid-cols-2 gap-4">
        {data.length > 0 ? (
          <>
            <div className="bg-white p-4 rounded shadow-md">
              <h2 className="text-lg font-semibold mb-2 text-center">Bar Chart</h2>
              <BarChart data={data} />
            </div>
            <div className="bg-white p-4 rounded shadow-md">
              <h2 className="text-lg font-semibold mb-2 text-center">Line Chart</h2>
              <LineChart data={data} />
            </div>
          </>
        ) : (
          <p>No data available.</p>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
