import React, { useEffect, useState } from "react";
import { useCookies } from "react-cookie";
import api from "../utils/api"; // Use the custom axios instance
import FilterBar from "../components/FilterBar";
import BarChart from "../components/BarChart";
import LineChart from "../components/LineChart";

const Dashboard = () => {
  const [data, setData] = useState([]);
  const [filters, setFilters] = useState({
    age: "",
    gender: "",
    start_date: "",
    end_date: "",
  });
  const [cookies, setCookie] = useCookies(["filters"]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const { age, gender, start_date, end_date } = filters;

        const response = await api.get("/api/data", {
          params: { age, gender, start_date, end_date },
        });

        setData(response.data); 
        setCookie("filters", filters); 
      } catch (error) {
        console.error("Error fetching data:", error);
        alert("Failed to fetch data!");
      }
    };

    fetchData();
  }, [filters, setCookie]);

  return (
    <div className="min-h-screen bg-gray-50">
      {/* FilterBar component for setting filters */}
      <FilterBar filters={filters} setFilters={setFilters} />
      <div className="p-4">
        {/* BarChart for data visualization */}
        <BarChart data={data} />
        {/* LineChart for data trends */}
        <LineChart data={data} />
      </div>
    </div>
  );
};

export default Dashboard;
